import './bigSectionCard.css'
import {BigCard} from '../BigCard/BigCard'
import geroy from '../../pages/image/films/Герой наших снов/Герой наших снов визитка.jpg'
import friends from  '../../pages/image/films/Новая папка/all_days_repertoire_56500.jpg'
import onegin from '../../pages/image/films/Онегин/ОнегинВизитка.jpg'
import {useState} from "react";

export const films = [
    {
        key: 1,
        image: geroy,
        name: 'Герой наших снов',
        rental: '12 апреля - 3 мая',
        example: 'Geroy'
    },
    {
        key: 2,
        image: friends,
        name: 'Идеальные друзья',
        rental: '15 апреля - 6 мая',
        example: 'friends'
    },
    {
        key: 3,
        image: onegin,
        name: 'Онегин',
        rental: '22 апреля - 12 мая',
        example: 'onegin'
    }
]
export default function BigSectionCard({}) {
    const [keyStorage, setKeyStorage] = useState([0])
    return (
        <section className='contentFilm__bigSectionCard' >
            {films.map((film) => {
                return (
                    <BigCard src={film.image} key={film.key} name={film.name} rental={film.rental} onClick={() => localStorage.setItem('key', keyStorage)} example={film.example} style={{width: '32%'}}/>
                )
            })}
        </section>
    )
}
