import { BigCard } from "../BigCard/BigCard"
import { MiddleCard } from "../MiddleCard/MiddleCard"
import './sectionCard.css'
import love from '../../pages/image/films/Любовь со второго взгляда/Любовь СВВ.jpg'
import pila from '../../pages/image/films/Пила. Джокер/Пила. Джокер.jpg'
import season from '../../pages/image/films/Сезон охоты/Сезон охоты.jpg'
import forsage from '../../pages/image/films/Форсаж 8/форсаж 8.jpg'
import mushketer from '../../pages/image/films/Три мушкитёра/Три Мушкетера визитка.jpg'

export default function SectionCard ({className, classNameArticle, bigCardImg, middleCardImg, middleCardName, bigCardName, bigCardRental, middleCardRental}){
    const films = [
        {
            key: 1,
            image: love,
            name: 'Любовь со второго взгляда',
            rental: '15 апреля - 7 мая'
        },
        {
            key: 2,
            image: pila,
            name: 'Пила. Джокер',
            rental: '29 апреля - 25 мая'
        },
        {
            key: 3,
            image: season,
            name: 'Сезон охоты',
            rental: '21 апреля - 28 мая'
        },
        {
            key: 4,
            image: forsage,
            name: 'Форсаж 8',
            rental: '11 апреля - 23 мая'
        }
    ]
    return(
        <article className={classNameArticle}>
            <section className='contentFilm__bigCard'>
                <BigCard src={mushketer} name={'Три Мушкетёра'} rental={'3 мая - 5 июня'}/>
            </section>
            <section className={className}>
                {films.map((film) => {
                    return(
                        <MiddleCard key={film.key} src={film.image} name={film.name} rental={film.rental}/>
                    )
                })}
            </section>
        </article> 
    )
}
