import sliderImageThird from '../../pages/image/widgetImage/Онегин.webp'
import ButtonSlider from '../Button/ButtonSlider/ButtonSlider'
import './slider.css'

export function Slider(){
    return(
        <section className='slider' style={
                {
                    background: `url(${sliderImageThird}) 50% 0 no-repeat`,
                    backgroundSize: 'contain'
                }
            }>
                <p className='widgetText'>Бронирование по телефону:<br/> <strong>+8 800 535-35-35</strong></p>
                <nav className='btnSliderNav'>
                    <ButtonSlider></ButtonSlider>
                    <ButtonSlider></ButtonSlider>
                    <ButtonSlider></ButtonSlider>
                </nav>
            <div className="shadowBackgroundSlider"></div>
        </section>
    )
    
}
