import './buttonHeader.css'
import { useState } from 'react'

export default function ButtonHeader ({text, children, onClick, onChange, style, type}){
    const [active, setActive] = useState(false)

    function onClick (){
        setActive(!active)
    }
    return(
        <button
        onClick={onClick}
        onChange={onChange}
        style={style}
        type={type}
        isActive={active}
        className={active ?  "btnHeader btnHeaderActive" : "btnHeader"}
        >
        {text}{children}
        </button>
    )
}
