import './App.css';
import {Header} from './components/widgets/Header';
import {MainPage} from './components/pages/Main/Main';
import {ContactsPage} from './components/pages/Contacts/Contacts';
import {InformationPage} from './components/pages/Information/Information';
import {PricesPage} from './components/pages/prices/Prices';
import { useState } from 'react';



export function App() {
  const [nav, setNav] = useState('main')
  const [contactHeader, setcontactHeader] = useState([])
  
  const bak = {
    background: 'yellow'
  }
  const headerContact = {
    background: 'red'
  }

  function changeContact (current){
    setNav(current);
    if('onc'){
      setcontactHeader(headerContact)
    }
  }

  return (
    <>
      <Header style={contactHeader} active={nav} onChange={changeContact}>
      </Header>
      <main>
        
        {nav === 'main' && <MainPage/>}
        {nav === 'contacts' && <ContactsPage/>}
        {nav === 'info' && <InformationPage/>}
        {nav === 'prices' && <PricesPage/>}
      </main>
    </>
  );
}


export default App;
