import './buttonHeader.css'
import { useState } from 'react'

export default function ButtonHeader ({text, children, onClick, isActive, disabled, onChange, style, type}){

    return(
        <button
        onClick={onClick}
        onChange={onChange}
        style={style}
        type={type}
        isActive={isActive}
        className={active ?  "btnHeader btnHeaderActive" : "btnHeader"}
        >
        {text}{children}
        </button>
    )
}
